package leetcode.top150

object RepeatedSubstringPattern extends App {
  def repeatedSubstringPattern(s: String): Boolean = s.nonEmpty && {
    val length            = s.length
    var remaining: String = s.tail
    var prefix: String    = s.head.toString

    var consistSubstring: Boolean = false
    var canContinue               = true

    while (!consistSubstring && canContinue) {
      consistSubstring = remaining.startsWith(prefix) &&
        length % prefix.length == 0 &&
        remaining.replace(prefix, "").isEmpty

      canContinue = remaining.nonEmpty

      if (!consistSubstring && canContinue) {
        prefix += remaining.head
        remaining = remaining.tail
      }
    }

    consistSubstring
  }

  println(repeatedSubstringPattern("abab"))
  println(repeatedSubstringPattern("aba"))
  println(repeatedSubstringPattern("abcabcabcabc"))

  println(repeatedSubstringPattern("ababba"))

  println(
    repeatedSubstringPattern(
      ""
    )
  )
}
